import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent implements OnInit {

  constructor(){}
  ngOnInit(): void {

  }

  contactForm = new FormGroup({

    name: new FormControl("",[Validators.required,Validators.minLength(2),Validators.pattern("[a-zA-Z].*")]),
    email: new FormControl("",[Validators.required,Validators.email]),
    address: new FormControl("",[Validators.required,Validators.minLength(2),Validators.pattern("[a-zA-Z].*")]),

    mobile: new FormControl("",[Validators.required,Validators.pattern("[0-9]*"),
    Validators.minLength(10),
    Validators.maxLength(10)]),
    message: new FormControl("",[Validators.required])


  });

   submit(){
    console.log(this.contactForm.get( "name"));
   }

get name():FormControl{
  return this.contactForm .get("name") as FormControl;

}


get email():FormControl{
  return this.contactForm .get("email") as FormControl;


}
get address():FormControl{
  return this.contactForm .get("address") as FormControl;


}

get mobile():FormControl{
  return this.contactForm .get("mobile") as FormControl;


}
get message():FormControl{
  return this.contactForm .get("message") as FormControl;


}
}

<div class="contact-container">
  <h2 class="contact-title">Contact Us</h2>
  <form (ngSubmit)="submitForm()" #contactForm="ngForm" class="contact-form">
    <label for="name">Name</label>
    <input type="text" id="name" name="name" [(ngModel)]="contactFormModel.name" required>

    <label for="email">Email</label>
    <input type="email" id="email" name="email" [(ngModel)]="contactFormModel.email" required>

    <label for="message">Message</label>
    <textarea id="message" name="message" [(ngModel)]="contactFormModel.message" required></textarea>

    <button type="submit" class="submit-button">Submit</button>
  </form>

  <div *ngIf="submitted" class="thank-you-message">
    <p>Thank you for your message!</p>
    <p>Name: {{contactFormModel.name}}</p>
    <p>Email: {{contactFormModel.email}}</p>
    <p>Message: {{contactFormModel.message}}</p>
  </div>
</div>
