    <input type="" placeholder="write a subject" required>
    <textarea name="" id="" cols="10" rows="10" placeholder="your message" required></textarea>
    <input type=""  name="" value="submit" class="btn">
  </form>
  <div class="contacat-img">
    <img src="../../assets/Employee-Management-Software.jpg">
  </div>
</div>
