import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent implements OnInit {

  constructor(){}
  ngOnInit(): void {

  }

  contactFormModel = {
    name: '',
    email: '',
    message: ''
  };
  submitted = false;

  submitForm() {
    // Logic to submit the form to your backend server or perform any other desired action
    // Once the form is submitted successfully, set submitted to true to display the thank you message
    this.submitted = true;
  }
}


<div class="container mt-3 col-6">
  <h1 class="text-center">Contact Us</h1>
  <p class="text-center">If you have any questions or feedback, please feel free to contact us using the form below.</p>

  <form>
   <div class="form-row">
      <div class="form-group col-md-6">
        <label for="name">Name</label>
        <input type="text" class="form-control" id="name" name="name" required>
      </div>
      <div class="form-group col-md-6">
        <label for="email">Email</label>
        <input type="email" class="form-control" id="email" name="email" required>
      </div>
    </div>
    <div class="form-row">
      <div class="form-group col-md-6">
        <label for="phone">Phone</label>
        <input type="tel" class="form-control" id="phone" name="phone" required>
      </div>
      <div class="form-group col-md-6">
        <label for="message">Message</label>
        <textarea class="form-control" id="message" name="message" rows="4" required></textarea>
      </div>
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
  </form>




</div>
