import { Component } from '@angular/core';
import { Employee } from '../employee';
import { EmployeeService } from '../employee.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-employee',
  templateUrl: './create-employee.component.html',
  styleUrls: ['./create-employee.component.css']
})
export class CreateEmployeeComponent {

  employee: Employee = new Employee();
  constructor(private employeeService: EmployeeService,
    private router: Router) { }

  ngOnInit(): void {
  }

  saveEmployee(){
    this.employeeService.createEmployee(this.employee).subscribe( data =>{
      console.log(data);
      this.goToEmployeeList();
    },
    error => console.log(error));
  }

  goToEmployeeList(){
    this.router.navigate(['/employees']);
  }

  onSubmit(){
    console.log(this.employee);
    this.saveEmployee();
  }
}



<h3 class="text-center"> Add Employee </h3>
<div class="container">
<div class="col-md-6 offset-md-3">

  <form (ngSubmit) = "onSubmit()">

      <div class="form-group">
          <label> First Name</label>
          <input type="text" class ="form-control" id = "firstName"
              [(ngModel)] = "employee.firstName" name = "firstName">
      </div>
       <br>
      <div class="form-group">
          <label> Last Name</label>
          <input type="text" class ="form-control" id = "lastName"
              [(ngModel)] = "employee.lastName" name = "lastName">
      </div>
       <br>
      <div class="form-group">
          <label> Email Id</label>
          <input type="text" class ="form-control" id = "emailId"
              [(ngModel)] = "employee.emailId" name = "emailId">

      </div>

        <br>
      <div class="form-group">
        <label> Number</label>
        <input type="number" class ="form-control" id = "number"
            [(ngModel)] = "employee.number" name = "number">

    </div>
           <br>      <pre>
                   <button type="button" class="btn btn-success btn-lg">Submit</button>
                               </pre>

  </form>
  </div>
  </div>
  <div class="container-img">
    <img src="../../assets/Screenshot (216).png" alt="">
  </div>
