import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {


    username!: string;
    password!: string;

    onSubmit() {
      // Perform login logic here, e.g. send data to a backend API for authentication
      console.log('Login form submitted:', this.username, this.password);
      // Add your login logic here
    }
  }



<h2>Login </h2>

<div class="container">

  <form (ngSubmit)="onSubmit()">

    <div class="form-group col-6">
      <br>
      <br>
      <br>
      <pre>

                             <label for="username">Username:</label>
                            <input type="text" id="username" name="username" [(ngModel)]="username" class="form-control" required>
      </pre>
    </div>
    <br>
    <div class="form-group col-6">
      <label for="password">Password:</label>
      <input type="password" id="password" name="password" [(ngModel)]="password" class="form-control" required>
    </div>
    <br>
    <pre>
               <button type="submit" class="btn btn-primary">Login</button>
    </pre>
  </form>
</div>
<div class="containerimg">
  <img src="../../assets/login.png">
</div>
